import { NamespaceConsumer, NamespaceShape, withNamespace } from '@ux/namespace-component';
import React from 'react';
import PropTypes from 'prop-types';

/**
 * Tray close component.
 *
 *
 * @class TrayClose
 * @public
 */
class TrayClose extends NamespaceConsumer {
  /**
   * Render the component.
   *
   * @returns {React.ReactElement} close button.
   * @api public
   */
  render() {
    const props = this.props;
    return (
      <button className={ this.namespace('close') } onClick={ props.onClick }></button>
    );
  }
}

/**
 * Property definitions.
 *
 * @type {Object}
 * @public
 */
TrayClose.propTypes = {
  ...NamespaceShape,
  onClick: PropTypes.func.isRequired
};

export default withNamespace(TrayClose);
