import { Application } from 't3js';
import solid from '@buzzfeed/buzzblocks/js/services/solid/standalone';
import { ZONE1_BFNEWS } from '../../core/constants';
import { AdFormatBuzz } from '../../core';
import bigstoryModuleTemplate from './buzz-module.html';
import bigstoryContentTemplate from './buzz-content.html';
import bigstoryContentNewsTemplate from '../../skins/news/bsu.html';


class AdBigstoryBuzz extends AdFormatBuzz {
  get template() {
    if (this.skin === ZONE1_BFNEWS) {
      return bigstoryContentNewsTemplate;
    }
    return bigstoryContentTemplate;
  }

  getTemplateData() {
    const data = super.getTemplateData();
    data.breakpoint = solid.getBreakPoint();
    return data;
  }
}

AdBigstoryBuzz.moduleTemplate = bigstoryModuleTemplate;

Application.addModule('ad-format-bigstory-buzz', AdBigstoryBuzz.generateModule());


export { AdBigstoryBuzz };
