export default {
  /**
   * Validates email address
   * @param {String} email - email address
   * @returns {Boolean} true if email is valid, false otherwise
   */
  email(email) {
    // taken regex from http://emailregex.com
    /* eslint-disable */
    var regexp = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/i;
    /* eslint-enable */
    return regexp.test(email);
  },
  /**
   * Check if file has image type
   * @param {File} file - File object
   * @param {Array} [additionalTypes] - optional array of additional file types
   * @returns {boolean} - `true` if file is image, `false` - otherwise
   */
  isImage(file, additionalTypes) {
    var supported = ['image/jpg', 'image/jpeg', 'image/png', 'image/gif', ];
    additionalTypes = additionalTypes || [];
    if (!file || !file.type) {
      return false;
    }

    if (supported.indexOf(file.type) !== -1 || additionalTypes.indexOf(file.type) !== -1) {
      return true;
    }

    return false;
  }
};
